import React, { useState } from "react";
import axios from "axios";

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState("");
  const [operation, setOperation] = useState("sampling");
  const [tableHtml, setTableHtml] = useState("");
  const [error, setError] = useState("");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload", formData);
      setFilename(response.data.filename);
      setError(""); // Clear errors
      alert("File uploaded successfully!");
    } catch (err) {
      console.error("Upload Error:", err);
      setError("Failed to upload file.");
    }
  };

  const handleProcess = async () => {
    if (!filename) {
      setError("Please upload a file first.");
      return;
    }

    try {
      const response = await axios.post("http://127.0.0.1:5000/process", {
        filename: filename,
        operation: operation,
      });

      if (response.data.status === "success") {
        setTableHtml(response.data.table);
        setError(""); // Clear errors
      } else {
        setError(response.data.error || "Processing failed.");
      }
    } catch (err) {
      console.error("Processing Error:", err);
      setError("Error processing file. File might be incompatible.");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white shadow-lg rounded-lg p-6 w-full max-w-lg">
        <h2 className="text-2xl font-bold text-center mb-4">Upload & Process Data</h2>

        <input
          type="file"
          onChange={handleFileChange}
          className="border p-2 w-full rounded-md mb-4"
        />
        
        <button
          onClick={handleUpload}
          className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 rounded-md mb-4"
        >
          Upload File
        </button>

        <label className="block font-semibold mb-2">Select Operation:</label>
        <select
          value={operation}
          onChange={(e) => setOperation(e.target.value)}
          className="border p-2 w-full rounded-md mb-4"
        >
          <option value="sampling">Sampling</option>
          <option value="normalization">Normalization</option>
          <option value="pca">PCA</option>
          <option value="clustering">Clustering</option>
          <option value="market_analysis">Market Analysis</option>
        </select>

        <button
          onClick={handleProcess}
          className="w-full bg-green-500 hover:bg-green-600 text-white font-semibold py-2 rounded-md"
        >
          Process File
        </button>

        {error && <p className="text-red-500 mt-4 text-center">{error}</p>}

        {tableHtml && (
          <div
            dangerouslySetInnerHTML={{ __html: tableHtml }}
            className="mt-6 p-4 bg-gray-200 rounded-md overflow-x-auto"
          />
        )}
      </div>
    </div>
  );
};

export default FileUpload;
