import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [processedData, setProcessedData] = useState([]);
    const [downloadLink, setDownloadLink] = useState("");
    const [visualizationLink, setVisualizationLink] = useState("");

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        if (selectedFile && selectedFile.type === "text/csv") {
            setFile(selectedFile);
        } else {
            alert("Please upload a valid CSV file.");
        }
    };

    const handleUpload = async () => {
        if (!file) {
            alert("Please select a CSV file first.");
            return;
        }

        setLoading(true);
        setProcessedData([]);
        setDownloadLink("");
        setVisualizationLink("");
        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await axios.post("http://localhost:5000/upload", formData);
            setProcessedData(response.data.processed_data || []);
            setDownloadLink(response.data.download_link);
            setVisualizationLink(response.data.visualization_link);
        } catch (error) {
            console.error("Error uploading file:", error);
            alert("Failed to process the file.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="file-upload-container">
            <h2>Upload CSV File</h2>
            <input type="file" accept=".csv" onChange={handleFileChange} />
            <button onClick={handleUpload} disabled={loading}>
                {loading ? "Processing..." : "Upload and Process"}
            </button>

            {downloadLink && (
                <div>
                    <a href={downloadLink} download>
                        <button className="download-btn">Download Processed File</button>
                    </a>
                </div>
            )}

            {visualizationLink && (
                <div className="visualization-container">
                    <h3>Data Visualization</h3>
                    <img src={visualizationLink} alt="Histogram" />
                </div>
            )}

            {processedData.length > 0 && (
                <div className="table-container">
                    <h3>Processed Data</h3>
                    <table>
                        <thead>
                            <tr>
                                {Object.keys(processedData[0]).map((key) => (
                                    <th key={key}>{key}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {processedData.map((row, rowIndex) => (
                                <tr key={rowIndex}>
                                    {Object.values(row).map((value, colIndex) => (
                                        <td key={colIndex}>{String(value)}</td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default FileUpload;
