import React, { useState } from "react";
import axios from "axios";
import "./FileUpload.css";  // Import CSS file

const FileUpload = () => {
  const [file, setFile] = useState(null);
  const [filename, setFilename] = useState("");
  const [operation, setOperation] = useState("sampling");
  const [tableHtml, setTableHtml] = useState("");
  const [error, setError] = useState("");
  const [visualization, setVisualization] = useState(null);
//   const [loading, setLoading] = useState(false);
  const [processing, setProcessing] = useState(false);
  const [visualizing, setVisualizing] = useState(false);
  const [showVisualize, setShowVisualize] = useState(false);

  // 📂 Handle file selection
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setFilename("");
    setTableHtml("");
    setVisualization(null);
    setError("");
    setShowVisualize(false);
  };

  // 🚀 Upload file
  const handleUpload = async () => {
    if (!file) {
      setError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload", formData);
      setFilename(response.data.filename);
      setError("");
      alert("File uploaded successfully!");
    } catch (err) {
      console.error("Upload Error:", err);
      setError("Failed to upload file.");
    }
  };

  // 🛠️ Process file
  const handleProcess = async () => {
    if (!filename) {
      setError("Please upload a file first.");
      return;
    }

    setProcessing(true);
    setError("");
    setTableHtml("");
    setShowVisualize(false);

    try {
      const response = await axios.post("http://127.0.0.1:5000/process", {
        filename: filename,
        operation: operation,
      });

      if (response.data.status === "success") {
        setTableHtml(response.data.table);
        setShowVisualize(true);
      } else {
        setError(response.data.error || "Processing failed.");
      }
    } catch (err) {
      console.error("Processing Error:", err);
      setError("Error processing file. File might be incompatible.");
    } finally {
      setProcessing(false);
    }
  };

  // 📊 Generate visualization
  const handleVisualize = async () => {
    if (!filename) {
      setError("Please upload a file first.");
      return;
    }

    setVisualizing(true);
    setError("");
    setVisualization(null);

    try {
      const response = await axios.post("http://127.0.0.1:5000/visualize", {
        filename: filename,
        operation: operation,
      });

      if (response.data.status === "success") {
        setVisualization(`data:image/png;base64,${response.data.image}`);
      } else {
        setError(response.data.error || "Visualization failed.");
      }
    } catch (err) {
      console.error("Visualization Error:", err);
      setError("Error generating visualization.");
    } finally {
      setVisualizing(false);
    }
  };

  return (
    <div className="container">
      <h2>Upload & Process Data</h2>

      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload} className="upload-btn">Upload</button>

      <br />
      <label>Select Operation:</label>
      <select value={operation} onChange={(e) => setOperation(e.target.value)}>
        <option value="sampling">Sampling</option>
        <option value="normalization">Normalization</option>
        <option value="pca">PCA</option>
        <option value="clustering">Clustering</option>
        <option value="market_analysis">Market Analysis</option>
      </select>

      <button onClick={handleProcess} className="process-btn" disabled={processing}>
        {processing ? "Processing..." : "Process"}
      </button>

      {showVisualize && (
        <button onClick={handleVisualize} className="visualize-btn" disabled={visualizing}>
          {visualizing ? "Visualizing..." : "Visualize Data"}
        </button>
      )}

      {error && <p className="error">{error}</p>}

      {/* Display the processed table */}
      {tableHtml && (
        <div
          dangerouslySetInnerHTML={{ __html: tableHtml }}
          className="data-table"
        />
      )}

      {/* Display the generated visualization */}
      {visualization && (
        <div className="visualization">
          <h3>Visualization:</h3>
          <img src={visualization} alt="Data Visualization" style={{ width: "100%", maxHeight: "400px" }} />
        </div>
      )}
    </div>
  );
};

export default FileUpload;
